mapper.select:
select file_no, process, ref, file_name, save_path, save_name, file_ext, file_size, insert_user, insert_date
from file
where process = #{process} and ref = #{ref}
and kind = #{kind}
and file_no != #{exceptNo}
order by file_no

select file_no, process, ref, file_name, save_path, save_name, file_ext, file_size, insert_user, insert_date
from file
where file_no = #{fileNo}
