mapper.select:
select user_no, user_id, user_name, pwd, email, gender, delete_yn, insert_date
from user
where user_no=#{userNo}

mapper.update:
update user
set user_no = #{userNo}, user_id = #{userId}, user_name = #{userName}, pwd = #{pwd}, email = #{email}, gender = #{gender}, insert_date = now()
where user_no =#{userNo}

update user

  set delete_yn = 'Y'
where user_no =#{userNo}

mapper.select:
select user_no, user_id, user_name, pwd, email, gender, delete_yn, hash
from user
where 1=1 and user_id =#{userId}

select user_no, user_id, user_name, pwd, email, gender, delete_yn, insert_date, hash
from user
where 1=1 and user_no =#{userNo}
